mapper.insert:
INSERT INTO ASSET(
assetName
, created
, lastUpdate
, createId
, updateId
)VALUES(
#{dto.assetName}
,  NOW()
,  NOW()
, #{dto.createId}
, #{dto.updateId}
)

mapper.update:
UPDATE ASSET SET
assetName = #{dto.assetName}
,assetStatus = #{dto.assetStatus}
,updateId = #{dto.updateId}
,lastUpdate= NOW()
WHERE assetId= #{dto.assetId}
