mapper.insert:
INSERT INTO M_COMPLAINT(
title
, followStatus
, serviceCode
, created
, lastUpdate
, createId
, updateId
)VALUES(
#{dto.title}
, #{dto.followStatus}
, #{dto.serviceCode}
,  NOW()
,  NOW()
, #{dto.createId}
, #{dto.updateId}
)

mapper.select:
SELECT * FROM T_COMPLAINT WHERE tranSeq = 1
#{complaintCode}
ORDER BY complaintCode

SELECT * FROM T_COMPLAINT WHERE tranSeq = 2
#{parentComplaintCode}
ORDER BY parentComplaintCode

mapper.update:
UPDATE M_COMPLAINT SET
followStatus = #{dto.followStatus}
,updateId = #{dto.updateId}
,lastUpdate= NOW()
WHERE complaintCode= #{dto.complaintCode}
