mapper.insert:
INSERT INTO M_BUILDING(
buildingName
, totalFloor
, totalRoom
, description
, created
, lastUpdate
, createId
, updateId
)VALUES(
#{dto.buildingName}
, #{dto.totalFloor}
, #{dto.totalRoom}
, #{dto.description}
,  NOW()
,  NOW()
, #{dto.createId}
, #{dto.updateId}
)

mapper.update:
UPDATE M_BUILDING SET
buildingName = #{dto.buildingName}
, totalFloor = #{dto.totalFloor}
, totalRoom = #{dto.totalRoom}
, description = #{dto.description}
,updateId = #{dto.updateId}
,lastUpdate= NOW()
WHERE buildingCode= #{dto.buildingCode}

