mapper.insert:
INSERT INTO BUILDING(
buildingName
, memo
, created
, lastUpdate
, createId
, updateId
)VALUES(
#{dto.buildingName}
, #{dto.memo}
,  NOW()
,  NOW()
, #{dto.createId}
, #{dto.updateId}
)

mapper.update:
UPDATE BUILDING SET
buildingName = #{dto.buildingName}
,memo = #{dto.memo}
,updateId = #{dto.updateId}
,lastUpdate= NOW()
WHERE buildingId= #{dto.buildingId}
