mapper.insert:
INSERT INTO MEMBER(
fullName
, tel
, address
, sex
, birthday
, email
, idCard
, created
, lastUpdate
, createId
, updateId
)VALUES(
#{dto.fullName}
, #{dto.tel}
, #{dto.address}
, #{dto.sex}
, #{dto.birthday}
, #{dto.email}
, #{dto.idCard}
,  NOW()
,  NOW()
, #{dto.createId}
, #{dto.updateId}
)

mapper.update:
UPDATE MEMBER SET
fullName = #{dto.fullName}
, tel = #{dto.tel}
, address = #{dto.address}
, sex = #{dto.sex}
, birthday = #{dto.birthday}
, email = #{dto.email}
, idCard = #{dto.idCard}
,updateId = #{dto.updateId}
,lastUpdate= NOW()
WHERE memberId= #{dto.memberId}
