mapper.insert:
INSERT INTO M_NEWS(
newsType
, newHeader
, newShorter
, created
, lastUpdate
, createId
, updateId
)VALUES(
#{dto.newsType}
#{dto.newHeader}
, #{dto.newShorter}
,  NOW()
,  NOW()
, #{dto.createId}
, #{dto.updateId}
)

mapper.update:
UPDATE M_NEWS SET
newsType = #{dto.newsType}
,newHeader = #{dto.newHeader}
,newShorter = #{dto.newShorter}
,updateId = #{dto.updateId}
,lastUpdate= NOW()
WHERE newsCode= #{dto.newsCode}
