mapper.insert:
INSERT INTO M_NEWS(
newType
, newHeader
, newShorter
, created
, lastUpdate
, createId
, updateId
)VALUES(
#{dto.newType}
, #{dto.newHeader}
, #{dto.newShorter}
,  NOW()
,  NOW()
, #{dto.createId}
, #{dto.updateId}
)

mapper.update:
UPDATE M_NEWS SET
newType = #{dto.newType}
,newHeader = #{dto.newHeader}
,newShorter = #{dto.newShorter}
,updateId = #{dto.updateId}
,lastUpdate= NOW()
WHERE newCode= #{dto.newCode}

mapper.insert:
INSERT INTO T_NEW_DETAIL(
newCode
, refNewCode
, newContent
)VALUES(
#{dto.newCode}
, #{dto.refNewCode}
, #{dto.newContent}
)

mapper.update:
UPDATE T_NEW_DETAIL SET
refNewCode = #{dto.refNewCode}
,newContent = #{dto.newContent}
WHERE newContent= #{dto.newContent}
