mapper.insert:
INSERT INTO M_NEWS(
newType
, newHeader
, newShorter
, created
, lastUpdate
, createId
, updateId
)VALUES(
#{dto.newType}
, #{dto.newHeader}
, #{dto.newShorter}
,  NOW()
,  NOW()
, #{dto.createId}
, #{dto.updateId}
)

mapper.update:
UPDATE M_NEWS SET
newType = #{dto.newType}
,newHeader = #{dto.newHeader}
,newShorter = #{dto.newShorter}
,updateId = #{dto.updateId}
,lastUpdate= NOW()
WHERE newCode= #{dto.newCode}
