mapper.insert:
INSERT INTO NEWS(
title
, memo
, created
, lastUpdate
, createId
, updateId
)VALUES(
#{dto.title}
, #{dto.memo}
,  NOW()
,  NOW()
, #{dto.createId}
, #{dto.updateId}
)

mapper.update:
UPDATE NEWS SET
title = #{dto.title}
,memo = #{dto.memo}
,updateId = #{dto.updateId}
,lastUpdate= NOW()
WHERE newsId= #{dto.newsId}
